mapper.delete:

delete from MilkContainerInfo
where mcId = #{mcid,jdbcType=DECIMAL}
  and CompanyId = #{companyid,jdbcType=DECIMAL}
  and mcNo = #{mcno,jdbcType=VARCHAR}

mapper.select:

select mcId, CompanyId, mcNo, mcName, mcPosition, temperature, weight, updateDate, 
createDate, attrbute1, attrbute2, attrbute3, attrbute4, attrbute5
from MilkContainerInfo
where mcId = #{mcid,jdbcType=DECIMAL}
  and CompanyId = #{companyid,jdbcType=DECIMAL}
  and mcNo = #{mcno,jdbcType=VARCHAR}
